package BasicProgram;

public class Swapnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		int a=10;
		int b=20;
		int c=30;
		
		System.out.println("Before Swaping: a:"+a+" b: "+b+" c:"+c);
		 
		
		 
		//without temp
		
		
		  a=a+b;//30
		  b=a-b;//10
		  c=a-b;//20
		 
		
		System.out.println("After Swaping: a:"+a+" b: "+b+" c:"+c);
		

	}

}
